<?phpclass MySQL{	public $server,$user,$password,$db,$adb,$cdb,$wdb;	function __construct()	{			$this->setup();				mysql_connect($this->server,$this->user,$this->password);		$this->db('web');			}		function setup()	{			require_once("configure.php");		$this->server = $mysql_server;		$this->user = $mysql_user;		$this->password = $mysql_password;		$this->db = $db;		$this->adb = $adb;		$this->cdb = $cdb;		$this->wdb = $wdb;			}		function db($param1)	{			switch($param1)		{			case "characters" : $param1 = $this->cdb; break;			case "web" : $param1 = $this->db; break;			case "accounts" : $param1 = $this->adb; break;			case "world" : $param1 = $this->wdb; break;				}				mysql_select_db($param1);	}		function insert($db,$table,$variables,$values)	{		$error = "";		mysql_query("INSERT INTO `$db`.`$table` ($variables) VALUES ($values)") or $error = mysql_error();		if($error != "") return $error;		else return 1;	}			//a bit useless - lemme check some things	function select($db,$table,$what,$query)	{		//query can be "WHERE id = 1" or "ORDER ASC by `id`" and things like that		mysql_query("SELECT $what FROM `$db`.`$table` $query");	}}?>